import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { BottomNavigation, BottomNavigationAction } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import EmojiObjectsIcon from '@mui/icons-material/EmojiObjects';
import PersonIcon from '@mui/icons-material/Person';
import '../App.css';

const Dashboard = () => {
  const [userName, setUserName] = useState('Food Lover');
  const [trendingRecipes, setTrendingRecipes] = useState([]);
  const [navValue, setNavValue] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    // Simulate fetching trending recipes (replace with API call)
    setTrendingRecipes([
      { id: 1, name: 'Spicy Chicken Curry', image: 'https://www.sharmispassions.com/wp-content/uploads/2022/06/ChickenGravy1.jpg' },
      { id: 2, name: 'Vegan Avocado Toast', image: '/images/avocado.jpg' },
      { id: 3, name: 'Classic Margherita Pizza', image: '/images/pizza.jpg' },
    ]);
  }, []);

  const handleNavigation = (newValue) => {
    setNavValue(newValue);
    switch (newValue) {
      case 0:
        navigate('/home');
        break;
      case 1:
        navigate('/search');
        break;
      case 2:
        navigate('/ai');
        break;
      case 3:
        navigate('/profile');
        break;
      default:
        navigate('/home');
    }
  };

  return (
    <div className="dashboard">
      {/* Welcome Banner */}
      <div className="dashboard-banner">
        <h1>Welcome back, {userName}! üçΩÔ∏è</h1>
        <p>What are you craving today?</p>
      </div>

      {/* Mood-Based AI Suggestion Section */}
      <div className="dashboard-mood">
        <h2>‚ú® Get AI Recipe Suggestions Based on Your Mood!</h2>
        <button className="dashboard-mood-btn">Take Mood Quiz</button>
      </div>

      {/* Search Bar */}
      <div className="dashboard-search">
        <input type="text" placeholder="Search recipes..." />
        <button>üîç</button>
      </div>

      {/* Trending Recipes */}
      <div className="dashboard-trending">
        <h2>üî• Trending Recipes</h2>
        <div className="trending-list">
          {trendingRecipes.map((recipe) => (
            <div key={recipe.id} className="trending-card">
              <img src={recipe.image} alt={recipe.name} />
              <p>{recipe.name}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Bottom Navigation */}
      <BottomNavigation
        value={navValue}
        onChange={(event, newValue) => handleNavigation(newValue)}
        showLabels
        className="bottom-navigation"
      >
        <BottomNavigationAction label="Home" icon={<HomeIcon />} />
        <BottomNavigationAction label="Search" icon={<SearchIcon />} />
        <BottomNavigationAction label="AI" icon={<EmojiObjectsIcon />} />
        <BottomNavigationAction label="Profile" icon={<PersonIcon />} />
      </BottomNavigation>
    </div>
  );
};

export default Dashboard;
