import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import "../App.css";

const Home = () => {
  const [mood, setMood] = useState("");
  const [demoRecipe, setDemoRecipe] = useState(null);
  const navigate = useNavigate();

  // Sample AI-generated recipe for demo
  const sampleRecipe = {
    title: "Comforting Tomato Soup ðŸ…",
    description: "A warm and soothing tomato soup to lift your mood!",
    image: "tomato-soup.jpg",
  };

  // Handle AI demo search
  const handleFindRecipe = () => {
    if (mood.trim() !== "") {
      setDemoRecipe(sampleRecipe); // Show the demo recipe
    }
  };

  // Redirect to login if user clicks the recipe
  const handleRecipeClick = () => {
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className='bg'>
      <div className="d-flex flex-row hero-section">
        <div className='col-6 text-center'>
          <img src='name.png' className='name' alt="DishGenie Logo"/>
          <p className='h-line'>Lazy to cook? Out of ideas? Let DishGenie work its magic!</p>
          <div className='d-flex flex-row justify-content-center m-4'>
            <Link to="/register" className="btn m-3 button-r">Register</Link>
            <Link to="/login" className="btn m-3 button-l">Login</Link>
          </div>
        </div>
        <div className='col-6 m-5'>
          <img src="genie.png" className="dishgenie-image" alt="Landing Page"/>
        </div>
      </div>

      {/* AI Demo Search Section */}
      <div className="search-section text-center">
        <h2>Try DishGenieâ€™s AI Magic!</h2>
        <input 
          type="text" 
          placeholder="What's your mood...??" 
          className="search-input"
          value={mood}
          onChange={(e) => setMood(e.target.value)}
        />
        <button className="btn search-btn" onClick={handleFindRecipe}>
          Find Recipe
        </button>
      </div>

      {/* Show AI-Generated Demo Recipe */}
      {demoRecipe && (
        <div className="demo-recipe text-center" onClick={handleRecipeClick}>
          <h3>{demoRecipe.title}</h3>
          <p>{demoRecipe.description}</p>
          <img src={demoRecipe.image} alt={demoRecipe.title} className="demo-recipe-image"/>
          <p className="click-to-login">Click to unlock more recipes!</p>
        </div>
      )}

      <footer className="footer text-center">
        <p>Â© 2025 DishGenie. All Rights Reserved.</p>
        <p>
          <Link to="/about">About Us</Link> | 
          <Link to="/contact">Contact</Link> | 
          <Link to="/faq">FAQs</Link> | 
          <Link to="/privacy">Privacy Policy</Link>
        </p>
      </footer>
    </div>
  );
};

export default Home;
